SELECT temp1.*, temp2.TeacherId, temp2.TeacherName, temp2.Address, temp2.BirthyearTeacher, temp2.Gender FROM (SELECT temp.*, deg.Dept, deg.DegreeDescription, deg.TotalECTS FROM (SELECT co.CourseOfferId, co.CourseId, co.Year, co.Quartile, c.CourseName, c.CourseDescription, c.DegreeId, c.ECTS FROM CourseOffers co INNER JOIN Courses c ON co.CourseOfferId = 1 AND co.CourseId = c.CourseId) temp INNER JOIN Degrees deg ON temp.DegreeId = deg.DegreeId) temp1 INNER JOIN (SELECT tc.CourseOfferId, tc.TeacherId, t.TeacherName, t.Address, t.BirthyearTeacher, t.Gender FROM TeacherAssignmentsToCourses tc INNER JOIN Teachers t ON tc.CourseOfferId = 1 AND tc.TeacherId = t.TeacherId) temp2 ON temp1.CourseOfferId = temp2.CourseOfferId;
SELECT temp1.CourseOfferid, temp1.CourseId, temp1.Year, temp1.Quartile, temp2.StudentId, temp2.StudentName, temp2.Address, temp2.BirthyearStudent, temp2.Gender, temp2.DegreeId, temp2.Dept, temp2.DegreeDescription, temp2.TotalECTS FROM (SELECT co.CourseOfferId, co.CourseId, co.Year, co.Quartile, sa.StudentRegistrationId FROM StudentAssistants sa INNER JOIN CourseOffers co ON sa.StudentRegistrationId = 3 AND sa.CourseOfferId = co.CourseOfferId) temp1 INNER JOIN (SELECT temp.*, s.StudentName, s.Address, s.BirthyearStudent, s.Gender FROM (SELECT sd.StudentRegistrationId, sd.StudentId, d.* FROM StudentRegistrationsToDegrees sd INNER JOIN Degrees d ON sd.StudentRegistrationId = 3 AND sd.DegreeId = d.DegreeId) temp INNER JOIN Students s ON temp.StudentId = s.StudentId) temp2 ON temp1.StudentRegistrationId = temp2.StudentRegistrationId; 
SELECT AVG(Grade) FROM CourseRegistrations cr WHERE cr.StudentRegistrationId = 3;
